import React, { useState } from 'react';
import axios from 'axios';
import { Howl } from 'howler';
import AikoRunner from './components/AikoRunner';
import './App.css';

const jumpSound = new Howl({ src: ['sounds/jump.mp3'] });

function App() {
  const [score, setScore] = useState(0);
  const [highScore, setHighScore] = useState(0);

  const handleJump = () => {
    jumpSound.play();
    setScore(score + 1);
    axios.post('http://localhost:5000/api/score', score + 1)
      .then(res => setHighScore(res.data));
  };

  return (
    <div className="game-container" onClick={handleJump}>
      <h1>Aiko Run Game</h1>
      <p>Score: {score}</p>
      <p>High Score: {highScore}</p>
      <AikoRunner />
    </div>
  );
}

export default App;
