import React, { useEffect, useState } from 'react';
import { Howl } from 'howler';
import './AikoRunner.css';

// Load jump sound
const jumpSound = new Howl({ src: ['/sounds/jump.mp3'] });

const AikoRunner = () => {
  const [frame, setFrame] = useState(0);
  const [yPos, setYPos] = useState(20); // bottom position in px
  const [velocity, setVelocity] = useState(0);
  const [isJumping, setIsJumping] = useState(false);

  // Sprite sheet details
  const totalFrames = 10;   // total frames in the sheet
  const columns = 5;        // frames per row
  const frameWidth = 280;   // px
  const frameHeight = 385;  // px

  // Jump physics
  const gravity = 1.2;      // pull down speed
  const jumpStrength = 25;  // initial jump velocity

  // Animate running frames
  useEffect(() => {
    const interval = setInterval(() => {
      setFrame(prev => (prev + 1) % totalFrames);
    }, 80); // adjust speed
    return () => clearInterval(interval);
  }, []);

  // Handle jump physics
  useEffect(() => {
    let animation;
    const loop = () => {
      setYPos(prevY => {
        let newY = prevY + velocity;
        if (newY <= 20) { // landed
          newY = 20;
          setIsJumping(false);
          setVelocity(0);
        } else {
          setVelocity(v => v - gravity);
        }
        return newY;
      });
      animation = requestAnimationFrame(loop);
    };
    animation = requestAnimationFrame(loop);
    return () => cancelAnimationFrame(animation);
  }, [velocity]);

  // Jump trigger
  const handleJump = () => {
    if (!isJumping) {
      setIsJumping(true);
      setVelocity(jumpStrength);
      jumpSound.play();
    }
  };

  // Keyboard jump
  useEffect(() => {
    const onKeyDown = (e) => {
      if (e.code === 'Space' || e.code === 'ArrowUp') {
        handleJump();
      }
    };
    window.addEventListener('keydown', onKeyDown);
    return () => window.removeEventListener('keydown', onKeyDown);
  });

  // Calculate sprite sheet position
  const col = frame % columns;
  const row = Math.floor(frame / columns);

  return (
    <div
      className="aiko-runner"
      style={{
        backgroundPosition: `-${col * frameWidth}px -${row * frameHeight}px`,
        bottom: `${yPos}px`
      }}
      onClick={handleJump} // click to jump
    />
  );
};

export default AikoRunner;
