import React, { useState, useEffect} from 'react';
import axios from 'axios';
import AikoRunner from './components/AikoRunner';
import Obstacle from './components/Obstacle';
import GameOver from './components/GameOver';
import './App.css';


function App() {
  const [obstacleX, setObstacleX] = useState(window.innerWidth); // Start off-screen
  const [score, setScore] = useState(0);
  const [aikoY, setAikoY] = useState(20); // Aiko's bottom position
  const [aikoX, setAikoX] = useState(50); // Aiko's left position
  const [gameOver, setGameOver] = useState(false);
  const [jumping, setJumping] = useState(false);

  // Move obstacle
  useEffect(() => {
    if (gameOver) return;
    const interval = setInterval(() => {
      setObstacleX(prev => {
        if (prev < -160) { // obstacle width
          setScore(s => s + 1); // only if Aiko dodged
          return window.innerWidth; // reset obstacle
        }
        return prev - 5;
      });
    }, 30);
    return () => clearInterval(interval);
  }, [gameOver]);

  // Collision detection
  useEffect(() => {
    // --- FIX: Tighter Hitbox for Aiko's new smaller size ---
    const aikoHitbox = {
      width: 50,    // Reduced for new size
      height: 100,  // Reduced for new size
      paddingX: 45  // Adjusted to center the new hitbox
    };

    const aikoLeft = aikoX + aikoHitbox.paddingX;
    const aikoRight = aikoLeft + aikoHitbox.width;
    const aikoBottom = aikoY;

    // Obstacle's dimensions
    const obstacleWidth = 140;
    const obstacleHeight = 160;
    const obstacleLeft = obstacleX;
    const obstacleRight = obstacleX + obstacleWidth;
    const obstacleBottom = 20; // from Obstacle.css

    // Check for horizontal overlap
    const isHorizontallyOverlapping = aikoRight > obstacleLeft && aikoLeft < obstacleRight;

    // Check for vertical overlap (if Aiko is not high enough to clear the obstacle)
    const isVerticallyOverlapping = aikoBottom < obstacleBottom + obstacleHeight;

    if (isHorizontallyOverlapping && isVerticallyOverlapping) {
      setGameOver(true);
    }
  }, [obstacleX, aikoX, aikoY]); // Check on every position change

  const handleRestart = () => {
    setScore(0);
    setObstacleX(window.innerWidth);
    setGameOver(false);
    setAikoY(20);
    setAikoX(50);
  };

  return (
    <div className="game-container">
      <h1>Aiko Run Game</h1>
      <p>Score: {score}</p>
      {!gameOver && (
        <>
          <AikoRunner
            onJump={() => setJumping(true)}
            onLand={() => setJumping(false)}
            setY={setAikoY}
            setX={setAikoX} // Pass the setter function
            gameOver={gameOver} // Pass gameOver state
          />
          <Obstacle x={obstacleX} />
        </>
      )}
      {gameOver && <GameOver onRestart={handleRestart} />}
    </div>
  );
}

export default App;
