import React, { useState, useEffect} from 'react';
import axios from 'axios';
import { Howl } from 'howler';
import AikoRunner from './components/AikoRunner';
import Obstacle from './components/Obstacle';
import GameOver from './components/GameOver';
import './App.css';

const jumpSound = new Howl({ src: ['sounds/jump.mp3'] });

function App() {
  const [obstacleX, setObstacleX] = useState(800); // starting position
  const [score, setScore] = useState(0);
  const [highScore, setHighScore] = useState(0);
  const [gameOver, setGameOver] = useState(false);
  const [jumping, setJumping] = useState(false);

  // Move obstacle
  useEffect(() => {
    if (gameOver) return;
    const interval = setInterval(() => {
      setObstacleX(prev => {
        if (prev < -40) {
          setScore(s => s + 1); // only if Aiko dodged
          return 800; // reset obstacle
        }
        return prev - 5;
      });
    }, 30);
    return () => clearInterval(interval);
  }, [gameOver]);

  // Collision detection
  useEffect(() => {
    const checkCollision = () => {
      const aikoLeft = 50;
      const aikoRight = 50 + 280;
      const obstacleLeft = obstacleX;
      const obstacleRight = obstacleX + 40;

      const isHorizontallyOverlapping =
        aikoRight > obstacleLeft && aikoLeft < obstacleRight;
      const isVerticallyOverlapping = aikoY < 80; // if Aiko is low

      if (isHorizontallyOverlapping && isVerticallyOverlapping) {
        setGameOver(true);
      }
    };

    const interval = setInterval(checkCollision, 50);
    return () => clearInterval(interval);
  }, [obstacleX, aikoY]);

  const handleRestart = () => {
    setScore(0);
    setObstacleX(800);
    setGameOver(false);
  };

  return (
    <div className="game-container">
      <h1>Aiko Run Game</h1>
      <p>Score: {score}</p>
      {!gameOver && (
        <>
          <AikoRunner
            onJump={() => setJumping(true)}
            onLand={() => setJumping(false)}
            setY={setAikoY}
          />
          <Obstacle x={obstacleX} />
        </>
      )}
      {gameOver && <GameOver onRestart={handleRestart} />}
    </div>
  );
}

export default App;
