import React, { useEffect, useState } from 'react';
import './AikoRunner.css';

const AikoRunner = () => {
  const [frame, setFrame] = useState(0);
  const [position, setPosition] = useState(50); // initial left position

  const totalFrames = 10;
  const columns = 5; // 5 frames per row in your sprite sheet
  const frameWidth = 280; // px (match your CSS)
  const frameHeight = 385; // px

  useEffect(() => {
    const interval = setInterval(() => {
      setFrame(prev => (prev + 1) % totalFrames);
      setPosition(prev => {
        // If runner goes off screen, reset to start
        if (prev + frameWidth > window.innerWidth) {
          return 50;
        }
        return prev + 10;
      });
    }, 80);
    return () => clearInterval(interval);
  }, []);

  // Calculate column (x) and row (y) for current frame
  const col = frame % columns;
  const row = Math.floor(frame / columns);

  return (
    <div
      className="aiko-runner"
      style={{
        backgroundPosition: `-${col * frameWidth}px -${row * frameHeight}px`,
        left: `${position}px`
      }}
    />
  );
};

export default AikoRunner;
